sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '' || newValue === oldValue) {
		g_form.hideFieldMsg('u_rental_duration', true);
		return;
	}

	// Use the AJAXRentalPriceUtil utility to set field message for u_rental_duration
	var ga = new GlideAjax('AJAXRentalPriceUtil');
		ga.addParam('sysparm_name', 'durationClient');
		ga.addParam('sysparm_start', g_form.getValue('u_rental_start'));
		ga.addParam('sysparm_end', g_form.getValue('u_rental_end'));
		ga.addParam('sysparm_schedule', '8b7feb750f464300cab6306be1050e71');
		ga.addParam('sysparm_number', g_form.getValue('number'));
		ga.getXML(setDurationMessage);

	function setDurationMessage(response) {
		var result = response.responseXML.getElementsByTagName("result");
		var hrs =   result[0].getAttribute("hrs");
		var msg = 'Will be ' + hrs + ' Hours upon update';
		g_form.hideFieldMsg('u_rental_duration', true);
		g_form.showFieldMsg('u_rental_duration', msg, 'info', false);
		setPriceMessage(result[0].getAttribute("gdt"));
	}

	// Use the AJAXRentalPriceUtil utility to set field message for u_estimated_price	
	function setPriceMessage (response) {
		var ga = new GlideAjax('AJAXRentalPriceUtil');
			ga.addParam('sysparm_name', 'price');
			ga.addParam('sysparm_duration', response);
			ga.addParam('sysparm_equipment', g_form.getValue('u_equipment'));
			ga.getXML(setFieldMessage);

		function setFieldMessage(response) {
			var answer = response.responseXML.documentElement.getAttribute("answer");
			console.log('setFieldMessage answer is' + answer + ' and typeof ' + typeof answer);
			var msg = 'Will be $' + Number(answer).toFixed(2) + ' upon update';
			g_form.hideFieldMsg('u_estimated_price', true);
			g_form.showFieldMsg('u_estimated_price', msg, 'info', false);
		}
	}
}
